/* eslint-disable */
export default {
  imgUrl:{
    empty: 'data:image/png;base64,'
  },
  concateUrl(url1, url2){
      if(!url1 || !url1.length || url1.length == 0 || url1.trim().length == 0){
          return url2;
      }else if(!url2|| !url2.length || url2.length == 0 || url2.trim().length == 0){
          return url1;
      }else{
          url1 = url1.trim();
          url2 = url2.trim();
          if(url1[url1.length -1] == '/' && url2[0] == '/'){
              return url1 + url2.substring(1);
          }else if(url1[url1.length - 1] != '/' && url2[0] != '/'){
              return url1 + '/' + url2;
          }else{
              return url1 + url2;
          }
      }
  },
  // 判断是手机登录还是电脑登录
  browserRedirect() {
      var sUserAgent = navigator.userAgent.toLowerCase();
      var bIsIpad = sUserAgent.match(/ipad/i) == "ipad";
      var bIsIphoneOs = sUserAgent.match(/iphone os/i) == "iphone os";
      var bIsMidp = sUserAgent.match(/midp/i) == "midp";
      var bIsUc7 = sUserAgent.match(/rv:1.2.3.4/i) == "rv:1.2.3.4";
      var bIsUc = sUserAgent.match(/ucweb/i) == "ucweb";
      var bIsAndroid = sUserAgent.match(/android/i) == "android";
      var bIsCE = sUserAgent.match(/windows ce/i) == "windows ce";
      var bIsWM = sUserAgent.match(/windows mobile/i) == "windows mobile";
      if (bIsIpad || bIsIphoneOs || bIsMidp || bIsUc7 || bIsUc || bIsAndroid || bIsCE || bIsWM) {
          // alert("我是手机登录");
          window.location.href = mobileAddress;
      } else {
          // alert("我是电脑登录");
      }
  },
  // 保留几位小数
  toDecimal(val, s) {
    const str = val.toString();
    const index = str.indexOf('.');
    if (index >= 0 && str.length > index + s + 1) {
      if ('56789'.indexOf(str[index + s + 1]) >= 0) {
        const temp = str.substring(0, index + s + 2);
        let result = '';
        let carryBit = true;
        for (let i = temp.length - 2; i >= 0; i -= 1) {
          const num = temp[i];
          if (carryBit) {
            const idx = '0123456789'.indexOf(num);
            if (idx === 9) {
              result = `0${result}`;
              carryBit = true;
            } else if (idx >= 0) {
              result = '0123456789'[idx + 1] + result;
              carryBit = false;
            } else {
              result = num + result;
            }
          } else {
            result = num + result;
          }
        }
        if (carryBit) {
          result = `1${result}`;
        }
        return Number(result);
      }
      return Number(str.substring(0, index + s + 1));
    }
    return Number(val);
  },
  // 判断模板是否显示
  isVisible(attachments, type2) {
    let flag = false;
    if (!attachments) return;
    attachments.forEach(item => {
      if (item.type && item.type.indexOf(type2) !== -1) {
        flag = true;
      }
    });
    // eslint-disable-next-line consistent-return
    return flag;
  },
  // 获取当前的项目的URL
  getCurrentUrl() {
    const href = window.location.href;
    const index = href.indexOf('/#/openAccount');
    const currentHref = href.slice(0, index);
    return currentHref;
  },
  pictureLazyLoading() {
    // 一开始没有滚动的时候，出现在视窗中的图片也会加载
      start();
      // 当页面开始滚动的时候，遍历图片，如果图片出现在视窗中，就加载图片
      var clock; //函数节流
      $(window).on('scroll',function(){
          if(clock){
              clearTimeout(clock);
          }
          clock = setTimeout(function(){
              start()
          },200)
      })
      function start(){
            $('.assets-content img').not('[data-isLoading]').each(function () {
              if (isShow($(this))) {
                  loadImg($(this));
              }
          })
      }
      // 判断图片是否出现在视窗的函数
      function isShow($node){
          return $node.offset().top <= $(window).height()+$(window).scrollTop();
      }
      // 加载图片的函数，就是把自定义属性data-src 存储的真正的图片地址，赋值给src
      function loadImg($img){
        $img.attr('src', $img.attr('data-src'));
        // 已经加载的图片，我给它设置一个属性，值为1，作为标识
        // 弄这个的初衷是因为，每次滚动的时候，所有的图片都会遍历一遍，这样有点浪费，所以做个标识，滚动的时候只遍历哪些还没有加载的图片
        $img.attr('data-isLoading',1);
      }
  },
  getUrl() {
    var str = window.location.href;
    var index = str.indexOf('/'); // 字符出现的位置
    var num = 0; // 这个字符出现的次数
    while(index !== -1) {
        console.log(index); // 打印字符串出现的位置
        num++; // 每出现一次 次数加一
        if (num == 3) {
          console.log(index, 'index');
          return str.slice(0, index+1);
        }
        index = str.indexOf('/',index + 1); // 从字符串出现的位置的下一位置开始继续查找
    }
  },
  getUserIP(onNewIP) { //  onNewIp - your listener function for new IPs
    //compatibility for firefox and chrome
    var myPeerConnection = window.RTCPeerConnection || window.mozRTCPeerConnection || window.webkitRTCPeerConnection;
    var pc = new myPeerConnection({
      iceServers: []
    }),
    noop = function() {},
    localIPs = {},
    ipRegex = /([0-9]{1,3}(\.[0-9]{1,3}){3}|[a-f0-9]{1,4}(:[a-f0-9]{1,4}){7})/g,
    key;
    function iterateIP(ip) {
        if (!localIPs[ip]) onNewIP(ip);
        localIPs[ip] = true;
    }
      //create a bogus data channel
    pc.createDataChannel("");
    // create offer and set local description
    pc.createOffer().then(function(sdp) {
        sdp.sdp.split('\n').forEach(function(line) {
            if (line.indexOf('candidate') < 0) return;
            line.match(ipRegex).forEach(iterateIP);
        });
        pc.setLocalDescription(sdp, noop, noop);
    }).catch(function(reason) {
        // An error occurred, so handle the failure to connect
    });
    //sten for candidate events
    pc.onicecandidate = function(ice) {
        if (!ice || !ice.candidate || !ice.candidate.candidate || !ice.candidate.candidate.match(ipRegex)) return;
        ice.candidate.candidate.match(ipRegex).forEach(iterateIP);
    };
  },
  formatDate(value) { // 时间戳转换日期格式方法
    if (value == null) {
        return '';
    } else {
        let date = new Date(value);
        let y = date.getFullYear();// 年
        let MM = date.getMonth() + 1;// 月
        MM = MM < 10 ? ('0' + MM) : MM;
        let d = date.getDate();// 日
        d = d < 10 ? ('0' + d) : d;
        let h = date.getHours();// 时
        h = h < 10 ? ('0' + h) : h;
        let m = date.getMinutes();// 分
        m = m < 10 ? ('0' + m) : m;
        let s = date.getSeconds();// 秒
        s = s < 10 ? ('0' + s) : s;
        // return y + '-' + MM + '-' + d + ' ' + h + ':' + m + ':' + s;
        return y + '-' + MM + '-' + d;
    }
  }
};
/* eslint-enable */
